                {
                    mid = ((j-1)-i)/2;
                    for(k=1; k<=mid; k++)
                    {
                        dominoes[i+k] = dominoes[i];
                        dominoes[j-k] = dominoes[j];
                    }
                }
                else if(dominoes[i]=='L' && dominoes[j]=='L')
                {
                    for(k=i+1; k<j; k++)
                        dominoes[k]='L';
                }
                else if(dominoes[i]=='R' && dominoes[j]=='R')
                {
                    for(k=i+1; k<j; k++)
