            if(s[i] == '.'){
                if(l[i] == r[i]) ans += '.';
                else if(l[i] < r[i]) ans += 'L';
                else if(l[i] > r[i]) ans += 'R';
            }
            else ans += s[i];
        }
        return ans;
    }
};
