class Solution {
public:
    string pushDominoes(string dominoes) {
        int n = dominoes.size();
        vector<int> left(n, INT_MAX), right(n, INT_MAX);

        for (int i = 0, r = -1, l = -1; i < n; ++i) {
            // Right fall
            if (dominoes[i] == 'R') r = i;
            if (dominoes[i] == 'L') r = -1;
            if (dominoes[i] == '.' && r != -1) right[i] = i - r;

            // Left fall
            if (dominoes[n-i-1] == 'L') l = n-i-1;
            if (dominoes[n-i-1] == 'R') l = -1;
            if (dominoes[n-i-1] == '.' && l != -1) left[n-i-1] = l-n+i+1;
        }

        for (int i = 0; i < n; ++i)
            if (left[i] != right[i] && dominoes[i] == '.')
                dominoes[i] = left[i] < right[i] ? 'L' : 'R';

        return dominoes;
    }
};
