    {
        vector<int> nums(dominoes.size());
        for(int i = 1; i < dominoes.size(); i++) 
        {
            if(dominoes[i] == '.' && dominoes[i - 1] == 'R')
            {
                nums[i] = nums[i - 1] + 1;
                dominoes[i] = 'R';
