class Solution {
	//just for debugging
    void  print(unordered_map<int,char> &mp) {
        if (mp.size() < 1) {
            return;
        }
        cout<<"\n";
        unordered_map<int, char>:: iterator it;
        for(it = mp.begin(); it != mp.end(); it++) {
            cout<<"["<<it->first<<" "<<it->second<<"],";
        }

    }
public:

    string pushDominoes(string dominoes) {
        queue<int> q;
        for(int i = 0; i < dominoes.size(); i++) {
            if (dominoes[i] == 'R' || dominoes[i] == 'L') {
                q.push(i);
            }
        }
        /*
		* every iteration of while loop is one second simulated. In that one second we will exert force on neighbour dominoe
		* A Map is used to track what force is exerted on a specific neigbour
		* if in that particular second we have opposing force acting on the neigbour then object would remain stand still 
		*/
		while (q.size() > 0) {
            int curSize = q.size();
            unordered_map<int, char> mp;    
            unordered_map<int, char>:: iterator it;   //int represent index of neigbour and char represent force acting on it in next second

            for (int i = 0; i < curSize; i++) {
                int pos = q.front(); q.pop();


                if (dominoes[pos] == 'R' && (pos < dominoes.size() - 1) && dominoes[pos+1] == '.') {
                    it = mp.find(pos + 1);
					// here we evalute if there is already a force acting on the neigbour. If opposing force is present we remove it from the map
                    if (it != mp.end() && it->second =='L') {
                        mp.erase(it);
                    } else {
                        mp[pos+1] = 'R';
                    }

                }
                if (dominoes[pos] == 'L' && pos > 0 && dominoes[pos - 1] == '.') {
                    it = mp.find(pos - 1);
					// here we evalute if there is already a force acting on the neigbour. If opposing force is present we remove it from the map
                    if (it != mp.end() && it->second =='R') {
                        mp.erase(it);
                    } else {
                        mp[pos-1] = 'L';
                    }
                }
            }
            //print(mp);
			// This step we will setup for simulating the next second;
            for (it = mp.begin(); it != mp.end(); it++) {
                    dominoes[it->first] = it->second;
                    q.push(it->first);
            }
        }

        return dominoes;
    }
};
