class Solution {
public:
    string pushDominoes(string dominoes) {
        int n = dominoes.length();
        string finalDominoes(n, '#'), leftApplied = dominoes, rightApplied = dominoes;
        for(int i = n-1; i > 0; i--) {
            if(leftApplied[i] == 'L' and leftApplied[i-1] == '.')
                leftApplied[i-1] = 'L';
        }
        for(int i = 0; i < n-1; i++) {
            if(rightApplied[i] == 'R' and rightApplied[i+1] == '.')
                rightApplied[i+1] = 'R';
        }
        queue<int> windows; int windowCount = 0; 
        for(int i = 0; i < n; i++) {
            if(leftApplied[i] == rightApplied[i])
                finalDominoes[i] = leftApplied[i];
            else if(leftApplied[i] == '.')
                finalDominoes[i] = rightApplied[i];
            else if(rightApplied[i] == '.')
                finalDominoes[i] = leftApplied[i];
            else {
                windowCount += 1; continue;
            }
            if(windowCount)
                windows.push(windowCount); 
            windowCount = 0;
        }
        char prev = '\0';
        for(int i = 0; i < n; i++) {
            if(finalDominoes[i] == '#') {
                int len = windows.front(); windows.pop();
                if(len == 1) {
                    finalDominoes[i] = '.';
                } else {
                    int j = i;
                    while(j < i + (len/2)) {    
                        finalDominoes[j] = prev;
                        j += 1;
                    }
                    if(len & 1) {
                        finalDominoes[j] = '.'; j += 1;
                    }
                    char remaining = (prev == 'L')? 'R': 'L';
                    while(finalDominoes[j] == '#') {
                        finalDominoes[j] = remaining;
                        j += 1;
                    }
                    i = j-1;
                }
            }
            prev = finalDominoes[i];
        }
        return finalDominoes;
    }
};
