class Solution {
public:
    void func(string& s, int &last, int &idx, int &n) {
        if(last == -1 and idx == n) return;
        else if(last == -1) {
            if(s[idx] == 'L') {
                for(int i=0;i<idx;i++) s[i] = 'L';
            }
        }
        else if(idx == n) {
            if(s[last] == 'R') {
                for(int i = last +1;i<n;i++) s[i] ='R';
            }
        }
        else {
            if(s[last] == 'R' and s[idx] == 'L') {
                int no_dom = idx - last - 1;
                for(int len = 1, i = last + 1; len <= (no_dom>>1);len++, i++)
                    s[i] = 'R';
                for(int len = 1, i = idx - 1; len <= (no_dom>>1);len++, i--)
                    s[i] = 'L';
            }
            else if(s[last] == 'R' and s[idx] == 'R') {
                for(int i=last + 1;i<idx;i++) s[i] = 'R';
            }
            else if(s[last] == 'L' and s[idx] == 'L') {
                for(int i=last + 1;i<idx;i++) s[i] = 'L';
            }
            else if(s[last] == 'L' and s[idx] == 'R') {
                return;
            }
        }
    }
    string pushDominoes(string s) {
        int n = s.size(), idx = 0, last_left = -1;
        while(idx < n and s[idx] == '.') idx++;
        while(idx < n) {
            func(s, last_left, idx, n);
            while(idx < n and s[idx] - '.') idx++;
            last_left = idx - 1;
            while(idx < n and s[idx] == '.') idx++;
        }
        func(s, last_left, idx, n);
        return s;
    }
};
