            elif dominoes[i] == 'R':
                left_force[i] = 0
            else:
                if(i+1 < n):
                    left_force[i] = max(left_force[i+1]-1, 0)

        return ''.join('.' if right_force[i] == left_force[i] else 'R' if right_force[i] > left_force[i] else 'L' for i in range(n))

