var pushDominoes = function(dominoes) {
  const arr = dominoes.split("");

  let currStart = 0;
  let pushedToRight = false;

  for (let i=0; i < arr.length; i++) {
    if (arr[i] === "L" && !pushedToRight) {
      for (let j = currStart; j < i; j++) {
        arr[j] = "L";
      }
      currStart = i + 1;
    } else if (arr[i] === "L" && pushedToRight) {
      const qnty = i - currStart - 1;
      if (qnty % 2 ) {
        for (let j=currStart+1; j<currStart+Math.round(qnty/2); j++) {
          arr[j] = "R";
        }
        for (let j=i-1; j>i-Math.round(qnty/2); j--) {
          arr[j] = "L";
        }
      } else {
        for (let j=currStart+1; j<currStart+qnty/2+1; j++) {
          arr[j] = "R";
        }
        for (let j=i-1; j>i-qnty/2-1; j--) {
          arr[j] = "L";
        }
      }
      currStart = i + 1;
      pushedToRight = false;
    } else if (arr[i] === "R") {
      if (pushedToRight) {
        for (let j=currStart; j<i; j++) {
          arr[j] = "R";
        }
      }
      pushedToRight = true;
      currStart = i;
    } else {
      if (i === arr.length-1 && pushedToRight) {
        for (let j=currStart+1; j<arr.length; j++) {
          arr[j] = "R"
        }
      }
    }
  }
  return arr.join("");
};
