```
class Solution {
public:
    string pushDominoes(string d) {
        vector<int> t1;
        vector<int> t2;
        int c1=1;
        int c2=1;
        int n = d.length();
        int i=0;
        while(i<n)
        {
            c1=1;
            if(d[i]=='R')
            {
                t1.push_back(0);
                while(i+1<n && d[i+1]=='.'){
                    t1.push_back(c1);
                    c1++;
                    i++;
                }
            }
            else{
                t1.push_back(0);
            }
            i++;
        }
        i = n-1;
        while(i>=0)
        {
            c2= 1;
            if(d[i]=='L')
            {
                t2.push_back(0);
                while(i-1>=0 && d[i-1]=='.'){
                    t2.push_back(c2);
                    c2++;
                    i--;
                }
            }
            else{
                t2.push_back(0);
            }
            i--;
        }
        reverse(t2.begin(),t2.end());
        string ans = "";
        for(int i=0;i<n;i++)
        {
            if(t1[i]==t2[i] && t1[i]!=0){
                ans+='.';
            }
            else if(t1[i]<t2[i]){
                if(t1[i]==0)
                {
                    ans+='L';
                }
                else{
                    ans+='R';
                }
            }
            else if(t1[i]>t2[i]){
                if(t2[i]==0)
                {
                    ans+='R';
                }
                else{
                    ans+='L';
                }
            }
            else{
                ans+=d[i];
            }
        }
        return ans;
    }
};
