                }
            }

        } 
        for(int i=0; i<n; i++){
            if(dominoes[i]=='R'||dominoes[i]=='L') continue;
          if((pre[i]==0&&suff[i]==0)||pre[i]==suff[i]) continue;

            if(pre[i]!=0&&suff[i]!=0){
                if(pre[i]>suff[i]) dominoes[i]='L';
                if(pre[i]<suff[i]) dominoes[i]='R';
                else continue;
            }
            else{

              if(pre[i]==0) dominoes[i]='L';
                else{
                    dominoes[i]='R';
                }
            }
        }

        return dominoes;
    }
};

