Memoization solution: the unordered map is replaced with a 2d vector mapping where key is now an index, so s==5
a==4 and so on..
This was done so handling of 2d DP array would be simple
```
class Solution {
    const int MOD = 1e9 + 7;   
    const vector<vector<int>> mappings{{1}, {0,2}, {0,1,3,4}, {2,4}, {0}, {0,1,2,3,4}};

    public:

    int f(int idx, int prev, vector<vector<int>> &dp)
    {
        if(idx == 0)
            return 1;

        if(dp[prev][idx] != -1)
           return dp[prev][idx];

        dp[prev][idx] = 0;
        for(auto c : mappings[prev])
            dp[prev][idx] = (dp[prev][idx] + f(idx - 1, c, dp)) % MOD;  

        return (dp[prev][idx]);
    }

    int countVowelPermutation(int n) {
        vector<vector<int>> dp(6, vector<int> (n+1, -1));
        int ans = f(n, 5, dp);
        return ans;
    }
};
