class Solution:
    def countVowelPermutation(self, n: int) -> int:

        MOD = 10**9+7
        vowels = ['a', 'e', 'i', 'o', 'u']
        vowel_transition = {
            'a': ['e'],
            'e': ['a', 'i'],
            'i': ['a', 'e', 'o', 'u'],
            'o': ['i', 'u'],
            'u': ['a']
        }

        # 26 columns for a to z characters easy access
        dp = [[0 for _ in range(26)] for _ in range(n + 1)]

        # base case
        for v in vowels: dp[1][ord(v) - ord('a')] = 1


        for i in range(2, n+1):
            for vowel in vowels:
                ord_index = ord(vowel) - ord('a')
                for prev_vowel in vowel_transition[vowel]:
                    dp[i][ord_index] = (dp[i][ord_index] + dp[i-1][ord(prev_vowel) - ord('a')]) % MOD


        ans = 0
        for v in vowels: ans = (ans + dp[n][ord(v) - ord('a')]) % MOD
        return ans

