class Solution {
    public int countVowelPermutation(int n) {
      int mod = 1000000007;
      long[][]combinations = new long[5][n];
        for (int i=0;i<=4;i++)combinations[i][0]=1;
        for (int i=1;i<n;i++){
              combinations[0][i]=combinations[1][i-1];
              combinations[1][i]=(combinations[0][i-1]+combinations[2][i-1])%mod;
              combinations[2][i]=(combinations[0][i-1]+combinations[1][i-1]+combinations[3][i-1]+combinations[4][i-1])%mod;
              combinations[3][i]=(combinations[2][i-1]+combinations[4][i-1])%mod;
              combinations[4][i]=combinations[0][i-1];
        }
        return (int)((combinations[0][n-1]+combinations[1][n-1]+combinations[2][n-1]+combinations[3][n-1]+combinations[4][n-1])%mod);
