#define ll long long int

class Solution {
public:
  ll mod = 1000000000 + 7;
  ll rec(vector<vector<ll>> &dp, ll i, ll j, ll n)
  {
    if (i == n)
      return 1;
    if (dp[i][j] != -1)
      return dp[i][j];
    if (j == 'a' - 'a')
      dp[i][j] = rec(dp, i + 1, 'e' - 'a', n);
    else if (j == 'e' - 'a')
      dp[i][j] = (rec(dp, i + 1, 'a' - 'a', n) + rec(dp, i + 1, 'i' - 'a', n)) % mod;
    else if (j == 'i' - 'a')
      dp[i][j] = (rec(dp, i + 1, 'a' - 'a', n) + rec(dp, i + 1, 'e' - 'a', n) + rec(dp, i + 1, 'o' - 'a', n) + rec(dp, i + 1, 'u' - 'a', n)) % mod;
    else if (j == 'o' - 'a')
      dp[i][j] = (rec(dp, i + 1, 'i' - 'a', n) + rec(dp, i + 1, 'u' - 'a', n)) % mod;
    else if (j == 'u' - 'a')
      dp[i][j] = rec(dp, i + 1, 'a' - 'a', n);
    return dp[i][j]%mod;
  }
  int countVowelPermutation(int n) {
    std::vector<std::vector<ll>> dp(n + 1, std::vector<ll> (27, -1));
    // dp[i][j] = number of permutations possible of i length, having the j vowel at ith position.
    return (rec(dp, 1, 'a' - 'a', n) + rec(dp, 1, 'e' - 'a', n) + rec(dp, 1, 'i' - 'a', n) + rec(dp, 1, 'o' - 'a', n) + rec(dp, 1, 'u' - 'a', n)) % mod;
  }
};
