class Solution {
public:

    const int mod = 1000000007;
    unordered_map<int,vector<int>> umap;

	//this recursive function takes current size and last letter of string form so.
	    int rec(int n, int l,int last){
		//base cases
        if(n == l) return 1;
        if(last == -1){
            return (rec(n,1,0)%mod + rec(n,1,1)%mod + rec(n,1,2)%mod + rec(n,1,3)%mod + rec(n,1,4)%mod ) % mod ;
        }

       // calculate for all the possible letter that can be placed after this
        vector<int> v = umap[last];
        int ans = 0;
        for(int i=0;i<v.size();i++){
            ans = (ans%mod + rec(n,l+1,v[i])%mod) % mod;
        }
        return  (ans % mod);
    }

    int countVowelPermutation(int n) {
         umap[0] = {1};
        umap[1] = {0,2};
        umap[2] = {0,1,3,4};
        umap[3] = {4,2};
        umap[4] = {0};

         return rec(n,0,-1);

    }
};
