        umap[1] = {0,2};
        umap[2] = {1,3};
        umap[3] = {2};
        umap[4] = {3,2};
        dp.resize(n+1,vector<int>(5,-1));

        // return rec(n,0,-1);

        for(int i=1;i<=n;i++){
            for(int j=0;j<5;j++){
                if(i == 1){
                    dp[i][j] = 1;
                }
                else{
                    vector<int> v = umap[j];
                    int ans = 0;
                    for(int l=0;l<v.size();l++){
                        ans = (ans%mod + dp[i-1][v[l]]%mod) % mod;
                    }
                    dp[i][j] = ans % mod;
                }
            }
        }

        int res = 0;
        for(int i=0;i<5;i++){
            res = (res%mod + dp[n][i]%mod) % mod;
        }
        return res;
    }
};
