class Solution {
    public int countVowelPermutation(int n) {
        int mod=1000_000_007;
        long dp[][]=new long[n][5];
        Arrays.fill(dp[0],1);

        for(int i=1;i<n;i++){
            for(int j=0;j<5;j++){
                if(j==0){                              // for 'a'
                    dp[i][j]=dp[i-1][1];
                }
                else if(j==1){                              // for 'e'
                    dp[i][j]=dp[i-1][0]+dp[i-1][2];
                }
                else if(j==2){                              // for 'i'
                    dp[i][j]=dp[i-1][0]+dp[i-1][1]+dp[i-1][3]+dp[i-1][4];
                }
                else if(j==3){                              // for 'o'
                    dp[i][j]=dp[i-1][2]+dp[i-1][4];
                }
                else{                              // for 'u'
                    dp[i][j]=dp[i-1][0];
                }
                dp[i][j]=dp[i][j]%mod;
            }
        }
		//just add all answers possible from vowels
        long ans=0;
        for(int j=0;j<5;j++)
            ans+=dp[n-1][j];

        return (int)(ans%mod);
