            dp[i][1] = (dp[i-1][0] + dp[i-1][2])%mod;   // words ending with 'e'
            dp[i][2] = (dp[i-1][1] + dp[i-1][3])%mod;   // words ending with 'i'
            dp[i][3] = (dp[i-1][2])%mod ;               // words ending with 'o'
            dp[i][4] = (dp[i-1][3] + dp[i-1][2])%mod;   // words ending with 'u'
        }
        long long ans = 0;
        for(int i = 0;i<5;i++) ans = (ans + dp[n][i])%mod;
        return ans;
    }

