        for(long[] e:dp)
            Arrays.fill(e,-1);

        long ans = recursion(n,5,dp);

        return (int)ans%mod;
    }

    long recursion(int n,int last,long[][] dp)
    {
        if(n==0)
            return 1L;

        if(dp[n][last]!=-1)
            return dp[n][last];

        long ans=0L;

        if(last==0)
            ans=recursion(n-1,1,dp);
        if(last==1)
            ans=recursion(n-1,0,dp)+recursion(n-1,2,dp);
        if(last==2)
            ans=recursion(n-1,0,dp)+recursion(n-1,1,dp)+recursion(n-1,3,dp)+recursion(n-1,4,dp);
        if(last==3)
            ans=recursion(n-1,2,dp)+recursion(n-1,4,dp);
        if(last==4)
            ans=recursion(n-1,0,dp);
        if(last==5)
            ans=recursion(n-1,0,dp)+recursion(n-1,1,dp)+recursion(n-1,2,dp)+recursion(n-1,3,dp)+recursion(n-1,4,dp);

        return dp[n][last] = ans%mod;
    }
}
