typedef long long int l;
class Solution {
public:
    map<char,int> m;
    int MOD=1e9+7;
    l recursion(int i,int n,char last,vector<vector<l>> &dp){
        if(i==n){
            return 1;
        }

        if(dp[n-i][m[last]]!=-1){
                return dp[n-i][m[last]];
        }
        if(last=='a')
                return dp[n-i][m[last]]=recursion(i+1,n,'e',dp)%MOD;

        if(last=='e')
                return dp[n-i][m[last]]=(recursion(i+1,n,'a',dp)+recursion(i+1,n,'i',dp))%MOD;

        if(last=='i')
                return dp[n-i][m[last]]=(recursion(i+1,n,'a',dp)+recursion(i+1,n,'e',dp)+recursion(i+1,n,'o',dp)+recursion(i+1,n,'u',dp))%MOD;

        if(last=='o')
                return dp[n-i][m[last]]=(recursion(i+1,n,'i',dp)+recursion(i+1,n,'u',dp))%MOD;

        return dp[n-i][m[last]]=recursion(i+1,n,'a',dp)%MOD;

    }
    int countVowelPermutation(int n) {
        vector<vector<l>> dp(n+1,vector<l>(6,-1));
        l ans=0;
        m['a']=1;  // this mapping for getting integer out of our vowels , for memoizing in array 
        m['e']=2;
        m['i']=3;
        m['o']=4;
        m['u']=5;
        // as we have done for the 0th index we are going for the next index which is 1 and the starting character as different vowels
        ans=(ans+recursion(1,n,'a',dp))%MOD;
        ans=(ans+recursion(1,n,'e',dp))%MOD;
        ans=(ans+recursion(1,n,'i',dp))%MOD;
        ans=(ans+recursion(1,n,'o',dp))%MOD;
        ans=(ans+recursion(1,n,'u',dp))%MOD;
        return ans;
    }
};
