```
class Solution {
public:
    int mod=1000000007;
    int countVowelPermutation(int n) {
        vector<vector<long long>> dp(n,vector<long long>(6,-1));
        return fun(dp,0,-1);
    }
    int fun(vector<vector<long long>>& dp,int i,int c)
    {
        if(i>=dp.size())
            return 1;

        if(dp[i][c+1]!=-1)
            return dp[i][c+1];

        long long t=0;
        if(c==-1)
        {
            for(int j=0;j<5;j++)
                t+=fun(dp,i+1,j)%mod;
        }
        else
        {
            vector<long long> v(5,0);
            v[0]=fun(dp,i+1,0)%mod;// a
            v[1]=fun(dp,i+1,1)%mod;// e
            v[2]=fun(dp,i+1,2)%mod;// i
            v[3]=fun(dp,i+1,3)%mod;// o
            v[4]=fun(dp,i+1,4)%mod;// u

            if(c==0)// a
                t=v[1]%mod; // e
            if(c==1)// e
                t=(v[0]+v[2])%mod;// a,i
            if(c==2)// i
                t=(v[0]+v[1]+v[3]+v[4])%mod;// a,e,o,u
            if(c==3)// 0
                t=(v[2]+v[4])%mod;// i,u
            if(c==4)// u
                t=v[0]%mod;// a
        }
        return dp[i][c+1]=t%mod;
    }
};
```

