class Solution {  // TC: O(N)  SC: O(N)
    private int add(int a,int b){return (int)(((long)a+b)%1000000007);}
    public int countVowelPermutation(int n) {
        int dp[][] = new int[n][5],i,j,res=0;
        for(j=0;j<5;j++) dp[0][j] = 1;
