        }
        return sum % mod;
    }
private:
    const int mod = (int)(7 + 1e9);
    vector<long long> matmul(const vector<long long>& m1, const vector<long long>& m2) {
        vector<long long> res(25, 0);
        for (int i = 0; i < 5; ++i) {
            for (int j = 0; j < 5; ++j) {
                for (int k = 0; k < 5; ++k) {
                    res[i*5+j] += m1[i*5+k]*m2[k*5+j]; 
                }
                res[i*5+j] %= mod;
            }
        }
        return res;
    }
    vector<long long> matpow(const vector<long long>& m, int n) {
