class Solution {
    char[] cc = {'a', 'e', 'i', 'o', 'u'};
    int MOD = 1_000_000_007;
    public int countVowelPermutation(int n) {
        long res = 0;
        Map<String, Long> memo = new HashMap<>();
        for (int i = 0; i < 5; i++) {
            res = (res + helper(1, cc[i], n, memo)) % MOD;
        }
        return (int) res;
    }
    long helper(int index, char c, int n, Map<String, Long> memo) {
        if (index == n) {
            return 1;
        }
        String str = index + " " + c;
        if (memo.containsKey(str)) {
            return memo.get(str);
        }
        long res = 0;
        if (c == 'a') {
            res = helper(index + 1, 'e', n, memo);
        }
        if (c == 'e') {
            res = (res + helper(index + 1, 'a', n, memo)) % MOD;
            res = (res + helper(index + 1, 'i', n, memo)) % MOD;
        }
        if (c == 'i') {
            for (int i = 0; i < 5; i++) {
                if (cc[i] != 'i') {
                    res = (res + helper(index + 1, cc[i], n, memo)) % MOD;
                }
            }
        }
        if (c == 'o') {
            res = (res + helper(index + 1, 'u', n, memo)) % MOD;
            res = (res + helper(index + 1, 'i', n, memo)) % MOD;
        }
        if (c == 'u') {
            res = (res + helper(index + 1, 'a', n, memo)) % MOD;
        }
        memo.put(str, res);
        return memo.get(str);
    }
}
