class Solution {
public:
    int countVowelPermutation(int n) {
        long t[n][5], mod=1e9+7;
        for (int i=0; i<n; i++) {
            for (int j=0; j<5; j++) {
                if (i==0)
                    t[i][j]=1;
                else if (j==0) // a
                    t[i][j]=t[i-1][1]; // e
                else if (j==1) // e
                    t[i][j]=(t[i-1][0]+t[i-1][2])%mod; // a,i
                else if (j==2) // i
                    t[i][j]=(t[i-1][0]+t[i-1][1]+t[i-1][3]+t[i-1][4])%mod; // a,e,o,u
                else if (j==3) // o
                    t[i][j]=(t[i-1][2]+t[i-1][4])%mod; // i,u
                else // u
                    t[i][j]=t[i-1][0]; // a
            }
        }
        return accumulate(t[n-1], t[n-1]+5, 0ll) % mod;
    }
};
