class Solution:

	def countVowelPermutation(self, n: int) -> int:
        mod = int(1e9) + 7
        # a,e,i,o,u=0,1,2,3,4
        choices={0: [1], 1: [0, 2], 2: [0, 1, 3, 4], 3: [2, 4], 4: [0]}

