      ans += help(n, idx + 1, 'i') + help(n, idx + 1, 'u');
      break;

    case 'u':
      ans += help(n, idx + 1, 'a');
      break;
    }

    return dp[idx][c - 'a'] = ans % mod;
  }

  int countVowelPermutation(int n) {
    long long int ans = 0;

    vector<char> vowels{'a', 'e', 'i', 'o', 'u'};
    memset(dp, -1, sizeof(dp));

    for (char &c : vowels)
      ans += help(n, 1, c);

    return ans % mod;
  }

};
```

