```java
public int longestMountain(int[] A) {
  int n = A.length;
  int maxLen = 0;
  int i = 0;
  while (i < n - 1) {
    int upStart = i;
    // Go up
    while (i < n - 1 && A[i] < A[i + 1]) ++i;
    if (i == upStart) { // does not move
      ++i; continue;
    } // i stops the peek
    // Go down
    int downStart = i;
    while (i < n - 1 && A[i] > A[i + 1]) ++i;
    if (i == downStart) {
      ++i; continue;
    } // i stops at the last in the subarray
    // Update length
    int len = i - upStart + 1;
    maxLen = Math.max(maxLen, len);
  }
  return maxLen;
}
