class Solution 
{
    /* First scan the array for peaks (i.e. a value where the previous and next value are both 
     * lower.)  Once a peak is found scan foreword and back to determine how wide this particular
     * peak is.  Return the max of the values found.
     */

    public int longestMountain(int[] A) 
    {
        int     ret = 0;
        int     n   = A.length - 1; 

        for( int i = 1; i < n; i++ )
        {
            if( A[ i - 1 ] < A[ i ] && A[ i ] > A[ i + 1 ] )
            {
                int     l = i;

                while( l > 0 && A[ l - 1 ] < A[ l ] )       l--;
                while( i < n && A[ i + 1 ] < A[ i ] )       i++;

                ret = Math.max( ret, i - l + 1 );
            }
        }

        return ret;
    }
}
