class Solution(object):
    def longestMountain(self, A):
        """
        :type A: List[int]
        :rtype: int
        """        
        peaks = []
        t = len(A)
        if t==0:
            return 0

        for i in xrange(1,t-1):
            if A[i-1]<A[i]>A[i+1]:
                peaks.append(i)

        maxLen = -1
        for i in peaks:        
            curLen=1
            j=i
            while j>0 and A[j-1]<A[j]:        
                j-=1
            curLen+=(i-j)

            j=i
            while j<t-1 and A[j+1]<A[j]:
                j+=1
            curLen+=(j-i)

            maxLen = max(curLen, maxLen)

        if maxLen==-1:
            return 0

        return maxLen
