class Solution:
    def longestMountain(self, arr: List[int]) -> int:
        longest_mountain = 0
        cur_mountain = 0
        NOTHING = 0
        GOING_UP = 1
        GOING_DOWN_AFTER_PEAK = 2
        state = NOTHING
        prev = arr[0]
        for a in arr[1:]:
            if a > prev:
                if state in (NOTHING, GOING_DOWN_AFTER_PEAK):
                    state = GOING_UP
                    cur_mountain = 1
                cur_mountain += 1
            elif a < prev:
                if state == GOING_UP:
                    state = GOING_DOWN_AFTER_PEAK
                if state == GOING_DOWN_AFTER_PEAK:
                    cur_mountain += 1
                    longest_mountain = max(cur_mountain, longest_mountain)
            elif a == prev:
                state = NOTHING
                cur_mountain = 0
            prev = a
        return longest_mountain
