class Solution {
    public int longestMountain(int[] arr) {

        int i = 0, start = -1, end = -1;
        boolean peak = false, valley = false;
        int largestMountain = 0;

        while(i < arr.length - 1){ 
            //You expect a continous increase and then a continous decrease
            if(arr[i] < arr[i + 1]){                
                start = i;
                peak = true;

                //Continous increase
                while(i < arr.length - 1 && arr[i] < arr[i + 1]){
                    i++;
                }

                //Continous decrease
                while(i < arr.length - 1 && arr[i] > arr[i + 1]){
                    valley = true;
                    i++;
                    end = i;
                }

                //If there is continous increase and continous decrease then only it's a mountain
                if(peak && valley) largestMountain = Math.max(largestMountain, end - start + 1);
                //NOTE: If all the elements would be continously increasing then also it's not a mountain

                peak = false; valley = false;
            }

            //Not a mountain if :
            //elements are not strictly increasing(parallel surface)
            //or decreasing continously
            else i++;
        }
        return largestMountain;
    }
}
