class Solution {
public:
    int longestMountain(vector<int>& arr) {

        int n = arr.size();
        int maxLen = 0;

        if(n<3){
            return maxLen;
        }

        for(int i=1;i<n-1;i++){

            if(arr[i-1] < arr[i] && arr[i] > arr[i+1]) {

                int lidx = i,ridx=i;
                int len = 1;

                while(lidx > 0 && arr[lidx] > arr[lidx-1]){
                    len++; lidx--;
                }
                while(ridx+1 < n && arr[ridx] > arr[ridx+1]){
                    len++; ridx++;
                }
                maxLen = max(maxLen,len);
            } 
        }
        return maxLen;
    }
};

