class Solution {
public:
    int longestMountain(vector<int>& A) {

        // base case - mountain must be greater than 3, so there is no mountain
        if(A.size() < 3)
            return 0;

        int longestMountain = 1;        // current largest mountain in our list
        int previousNumber = A[0];      // current number we are looking at
        int currentMountain = 1;        // current size of our mountain 
        bool isAscending = true;        // determine if we are ascending or descending in the mountain

        // There are potentially 4 cases that can occur within this problem
            // 1. We are ascending and we keep ascending - increment mountain and continue
            // 2. We are ascending, but the current number is smaller than previous - now we descend
                // 2a. Ascending, smaller number, but the current mountain is too small or the same
                // height, we do not swap to descending but instead reset the height
            // 3. We are descending and we keep descending - increment mountain and continue
            // 4. We are descending, but the current number is larger than previous - reset
            // to set our loop to the current (in case the tailend of this mountain is the start 
            // to a bigger mountain i.e. [{4, 6, {3}, 4, 5, 7, 5, 3, 2}])

        for(int x = 1; x < A.size(); ++x)
        {
            if(isAscending)
            {
                // if the number is larger, then keep ascending
                if(A[x] > previousNumber)
                {
                    ++currentMountain;
                }
                else
                {
                    // if the number is smaller, and the current mountain is large enough
                    // to start a descent, and the previous number is not the same number,
                    // i.e. [2, 2, 2], then begin our descent (!ascending)
                    if(currentMountain >= 2 && A[x] != previousNumber)
                    {
                        isAscending = false;
                        ++currentMountain;
                        longestMountain = max(currentMountain, longestMountain);
                    }
                    else
                    {
                        // either the mountain is too small to descend, or we hit the same
                        // height so we never really ascended, so reset to height of 1
                        currentMountain = 1;
                    }
                }

                // move our previous pointer forward 
                previousNumber = A[x];
            }
            else
            {
                // if the number is smaller, then keep descending
                if(A[x] < previousNumber)
                {
                    ++currentMountain;
                }
                else
                {
                    // if the number is larger, then we have reached the end of the descent
                    // and have finished this mountain, so reset to ascend, set the current height
                    // to 1 and move our current loop iterator back in case the end of this mountain
                    // is the start to a larger mountain
                    isAscending = true;
                    currentMountain = 1;
                    x--;
                }

                // set our max longest mountain each descent in case it grows to our current max
                longestMountain = max(currentMountain, longestMountain);

                // move our previous pointer forward
                previousNumber = A[x];
            }
        }

        // if our longest mountain is larger than or equal to 3 (valid mountain)
        // then return it's height, else simply return 0 (no valid mountain)
        return longestMountain >= 3 ? longestMountain : 0; 
    }
};
