**Solution 1** (two auxillary variables)
```
class Solution {
public:
    int longestMountain(vector<int>& A) 
    {
        if (A.size() < 3)
            return 0;        
        A.push_back(A.back());
        int v=0, m=0;

        for (int i=1; i<A.size(); ++i)
        {
            if ((A[i-1] <= A[i] && A[i] >= A[i+1]) && !(A[i-1] < A[i] && A[i] > A[i+1]))
                v=i+1;

            if (A[i-1] >= A[i]  && A[i] <= A[i+1])
            {
                if (i>v && A[v+1] > A[v])
                    m = max(i-v+1, m);
                v = i;
            }
        }     
		A.pop_back();
        return m;
       }
};
