class Solution {
public:
    int longestMountain(vector<int>& A) 
    {
        int p = 0, m = 0;
        bool asc = true;

        for (int i=1; i<A.size(); ++i)
        {
            if (i == p+1 && A[i] <= A[i-1])
                p++;
            else if (asc) 
            {
                if (A[i] == A[i-1]) 
                    p=i;
                else if (A[i] < A[i-1])                  
                    asc = false; 
            }
            else if (A[i] >= A[i-1])
            {
                asc = true;
                m = max(i-p, m);
                p = (A[i] >  A[i-1]) ? i-1: i;
            }
        }

        if (!asc) m = max(int(A.size())-p, m);     
        return m;
	 }
};
