class Solution {
    public int longestMountain(int[] A) {
        int res = 0;
        for(int i = 1; i < A.length; ){
            if(A[i] > A[i - 1]){
                int j = i;

                //go uphill
                while(j < A.length && A[j] > A[j - 1]){
                    j++;
                }

                if(j == A.length)
                    break;
                if(A[j] == A[j - 1]){
                    i = j + 1;
                    continue;
                }					

                //go downhill
                while(j < A.length && A[j] < A[j - 1]){
                    j++;
                }

                if(j - i > 1)
                    res = Math.max(res, j - i + 1);

                i = j;
            } else 
                i++;
        }
        return res;
    }
}
