	public:
		int longestMountain(vector<int>& arr) {
			int n=arr.size();
			vector<int> dp1(n,1),dp2(n,1),sum(n,0);
			for(int i=1;i<n;i++){
				if(arr[i]>arr[i-1] && 1+dp1[i-1]>dp1[i]) dp1[i]=1+dp1[i-1];
			}
			for(int i=n-2;i>=0;i--){
