			for(int i=0;i<n;i++){
				if(dp1[i]!=1 && dp2[i]!=1) sum[i]=dp1[i]+dp2[i];
			}  
			int ans=*max_element(sum.begin(),sum.end());
			if(ans) ans-=1;
			return ans;
		}
	};

