//works successfully on leetcode!

class Solution {
public:
    int longestMountain(vector<int>& arr) {

    //constraint given in the question
    if(arr.size() < 3)
        return 0;

    //start travelling from the beginning of the array
    int traveller = 0;
    int mountainStart, mountainPeak, mountainLength=0; //answer will be 0 when no mountain exists

    //travel till the end of the array
    while(traveller < arr.size())
    {
        //set the starting point of the current mountain 
        mountainStart = traveller;

        //climb the mountain (if an ascending slope exists)
        while(traveller + 1 < arr.size() and arr.at(traveller) < arr.at(traveller+1))
        {
            traveller++;
        }

        //if there was a descending slope, the traveller would be standing at the same place
        //hence start travelling from the next step
        if(traveller == mountainStart)
        {
            traveller++;
            continue;
        }

        //traveller is now standing on the mountain peak
        mountainPeak = traveller;

        //climb down the mountain (if an descending slope exists)
        while(traveller + 1 < arr.size() and arr.at(traveller) > arr.at(traveller+1))
        {
            traveller++;
        }

        //if it's a plain surface the traveller must not have moved
        //so take one step ahead and start finding the mountain again
        if(traveller == mountainPeak)
        {
            traveller++;
            continue;
        }

        //Find the longest mountain throughout the trip
        mountainLength = max(mountainLength, traveller - mountainStart + 1);
    }

    return mountainLength;
}
};
