class Solution {
    public int longestMountain(int[] arr) {

        int maxLen = 0, count = 1; //coz we included 1st element
        boolean inc = false, dsc = false; //maintain if we first increased and then decreased

        int i = 1;

        while (i < arr.length) {

            inc = false; dsc = false;
            count = 1;

            //increasing
            while (i < arr.length && arr[i] > arr[i-1]) {
                count++;
                i++;
                inc = true;
