            //decreasing, we will calculate only when we have increased flg is true
            while (inc && i < arr.length && arr[i] < arr[i-1]) {
                count++;
                i++;
                dsc = true;
            }

            //if values increased and decreased
            if (inc && dsc) {
                maxLen = Math.max(maxLen, count);
                count= 1;

            }else i++;

        }
        return maxLen>=3 ? maxLen : 0;     
