class Solution {
public:
    int longestMountain(vector<int>& nums) {
        int n = nums.size();
        vector<int> LISFront(n,1);
        vector<int> LISBack(n,1);
        int i = 1, j = n-2;
        for(int i = 1; i < n; i++)
        {
            int snd = 1;
            if(nums[i-1]< nums[i])
            {
                snd = max(snd, LISFront[i-1]+1);
            }
            LISFront[i] = snd;
            snd = 1;
            if(nums[j+1] < nums[j])
            {
                snd = max(snd, LISBack[j+1]+1);
            }
            LISBack[j] = snd;
            j--;
        }
        int ans = 0;
        for(int i = 0; i < n; i++)
        {
            if(LISBack[i] == 1 || LISFront[i] == 1) continue;
            ans = max(ans, LISBack[i]+LISFront[i]-1);
        }
        return ans;
    }
};
```

