class Solution {
public:
    int longestMountain(vector<int>& arr) {
        // ic 1 means we are looking for increasing sequence ic 0 means we are looking for decreasing sequence

// intially we want increasing sequence starting form i=0
        int ic=1,l=1,a=0;          
        for(int i=1;i<arr.size();i++){
            if(arr[i]>arr[i-1]){   // numbers in increasing sequence
                if(ic==1)l++;   // if we were looking for incresing then we found another number to add to our answer
                else{

/*


         /\
        /  \  /
            \/
             this point here marks the end of mountain
             what this means is that we were hoping this number to be less than the previous  one
			 but we found it greater than the prevoius one. 
			 this marks the end of our  current mountain and start of new mountain
*/
                  a=max(a,l); 
                    l=2;
                    ic=1;
                }
            }else if(arr[i]<arr[i-1]){
                if(ic==0)l++,a=max(a,l);
                else{
/*


          /\
         /  this point here marks the peak of the mountain.
            we wanted to continue going up hill but instead we found a slope.
            this makes this a mountain we check if this suitable( max ) answer and then we continue hoping to go downhill.

            \                                               /\
              this is not a peak                           this is a peak
                            we need atleast 3 numbers including current one 
*/     



                    if(l>1)l++,ic=0,a=max(a,l);
                    else l=1,ic=1;

                }
            }else{
                l=1;
                ic=1;
            }

        }
        return a;
    }
};
