class Solution {
    public int longestMountain(int[] a) {
        int largest = 0;
        int n=a.length;
        for(int i=1;i<n-1;){
            //for founding the peaks
            if(a[i]>a[i-1] && a[i]>a[i+1]){
                int count =1;
                int j=i;
                //for count in the backwards
                while(j>=1 && a[j]>a[j-1]){
                    j--;
                    count++;
                }
                 //for count in the forwards
                while(i<n-1 && a[i]>a[i+1]){
                    i++;
                    count++;
                }
                //checking if it is the largest peak;
                largest = Math.max(largest,count);
            }
            else
                i++;
        }
        return largest;
    }
}
