class Solution {
    public int longestMountain(int[] arr) {
        int res = 0;
        if(arr.length < 2) return 0;
        for(int i=1; i<arr.length-1;i++){
            boolean isPeak = false;
            if(arr[i] > arr[i-1] && arr[i] > arr[i+1]){
                isPeak = true;
