                int currPeak = 1;
                while(j>=0 && arr[j+1]>arr[j]){
                    currPeak++;
                    j--;
                }
                j=i+1;
                while(j<arr.length && arr[j-1] > arr[j]){
                    currPeak++;
                    j++;
                }
                 res = Math.max(currPeak,res);
            }  
        }
        return res;
    }
}
