class Solution {
    public int longestMountain(int[] a) {
        int n=a.length;
        int[] left=new int[n];
        for(int i=1; i<n; i++)
        {
            if(a[i]>a[i-1])
            {
                left[i]=left[i-1]+1;
            }
            else if(a[i]<a[i-1])
            {
                left[i]=0;
            }
        }
        int[] right=new int[n];
        for(int i=n-2; i>=0; i--)
        {
            if(a[i]>a[i+1])
            {
                right[i]=right[i+1]+1;
            }
            else if(a[i]<a[i+1])
            {
                right[i]=0;
            }
        }
        int ans=0;
        for(int i=0; i<n; i++)
        {
            if(left[i]>0 && right[i]>0)
            {
                ans=Math.max(ans, left[i]+right[i]+1);   
            }
        }
        return ans;
    }
}
