class Solution {
public:
    int longestMountain(vector<int>& arr) {
        int start = 0;
        int res = 0;

        while(start < arr.size())
        {
            int end = start; // init end to be the same position as start
            bool walked_up = false;
            bool walked_down = false;
            while(end + 1 < arr.size() && arr[end + 1] > arr[end]) // we can walk up if the next value is greater
            {
                walked_up = true; // we actually walked up.
                end++;
            }
            while(end + 1 < arr.size() && arr[end + 1] < arr[end]) // now we walk down
            {
                walked_down = true; // we actually walked down
                end++;
            }
            if (walked_up && walked_down) // it is only a valid mountain if we walked up and down.
            {
                res = max(res, end - start + 1);
            }
            start = max(end, start + 1); // the next index we look at is either the end of a mountain, or simply the next index.
        }
        return res;
    }
}
